    private void appendNumber(String num) {
    // ดึงข้อความปัจจุบันและลบ %
    String current = jTextField1.getText().replace("%", "");
    // ต่อเลขใหม่
    current += num;
    // เติม % กลับ
    jTextField1.setText(current + "%");
}                      

