// ปุ่มลบ (x)
private void jButtonxActionPerformed(java.awt.event.ActionEvent evt) {                                         
    String current = jTextField1.getText().replace("%", "");
    if (current.length() > 0) {
        current = current.substring(0, current.length() - 1);
    }
    jTextField1.setText(current + "%");
} 
      
